CREATE TABLE estabelecimentos(
  crmv Varchar(10),
  razao_social Varchar(200),
  cpf_cnpj Varchar(40),
  endereco text,
  contato_email Varchar(40),
  contato_telcel Varchar(40),
  latitude real,
  longitude real,
  id_relatorio Varchar(45) PRIMARY KEY,
  CONSTRAINT fk_id_relatorio
    FOREIGN KEY(id_relatorio)
        REFERENCES dados_fiscalizacao(id_relatorio)
);

